//vuex 설정 파일 
import { createStore } from "vuex";
import createPersistedState from 'vuex-persistedstate';

export default createStore({
    plugins: [
        createPersistedState({
          storage: window.sessionStorage, // store를 session storage에 유지
        })
    ],
    state: {
        //내용 추가하기 팝업
        addContent:false,

        //내용 수정하기 팝업
        editContent:false,
        editList :[],

        //카테고리 수정하기 팝업
        incomeEditCategory:false,
        expendEditCategory:false,

        //패턴 분석
        pattern: false,
        month : '04',

        income_label:[],
        income_data:[],
        expend_label:[],
        expend_data:[],
        
        //main화면
        account:'',



    }, 
    //상태 접근 (변경)
    mutations:{

        //계좌 클릭
        clickAccount(state, account){
            state.account= account
            console.log(state.account)
        },
        //내용 추가하기 팝업
        openAddContent (state) {
            console.log('add')
            state.addContent = true
        },
        closeAddContent (state) {
            state.addContent = false
        },

        //내용 수정하기 팝업
        openEditContent(state,list){
            state.editContent = true
            state.editList = list
        },
        closeEditContent(state,list){
            state.editContent = false
            state.editList = []
        },

        //카테고리 수정하기 팝업
        openEditCategory (state, kind) {
            console.log('edit')
            if(kind == 'income'){
                state.incomeEditCategory = true
                state.expendEditCategory = false
                console.log(state.incomeEditCategory)
            }
            else if(kind == 'expend'){
                state.expendEditCategory = true
                state.incomeEditCategory = false
                console.log(state.expendEditCategory)
            }
        },
        closeEditCategory (state) {
            console.log('삭제')
            state.incomeEditCategory = false
            state.expendEditCategory = false
        },

        clickPatternPopup(state) {
            state.pattern = ! state.pattern
            console.log(state.pattern )
        },
        setMonth(state,month){
            state.month = month
        },
        setExpendPattern(state, expend_label, expend_data) {
            state.expend_label = expend_label
            state.expend_data = expend_data
            console.log('store',expend_label)
        },
        setIncomePattern(state, income_label, income_data) {
            state.expend_label = income_label
            state.expend_data = income_data
            state.month = month
        }
    },
    modules:{

    }
});


// 참고 링크
